/////////////////////////////////////////////////////////////////////////////////////
// Draws an Oriented 'Bounding' Box
/////////////////////////////////////////////////////////////////////////////////////
bool gcgDrawOBox(VECTOR3 obbangles, VECTOR3 obbcenter, VECTOR3 obbsize) {
  // Check parameters
  if(obbangles == NULL || obbcenter == NULL || obbsize == NULL) {
    gcgReport(GCG_REPORT_CODE(GCG_ERROR, GCG_DOMAIN_MEMORY, GCG_BADPARAMETERS), "gcgDrawOBox(): Invalid parameters. (%s:%d)", basename((char*)__FILE__), __LINE__);
    return false;
  }

  MATRIX4 m;
  VECTOR4 quat; // Quaternion

  obbsize[0] *= 0.5;
  obbsize[1] *= 0.5;
  obbsize[2] *= 0.5;
  VECTOR3 obbmin = {-obbsize[0], -obbsize[1], -obbsize[2]};
  VECTOR3 obbmax = {obbsize[0], obbsize[1], obbsize[2]};

  // Build a quaternion from Euler angles
  gcgEULERTOQUATERNION(quat, (float) DEG2RAD(obbangles[0]), (float) DEG2RAD(obbangles[1]), (float) DEG2RAD(obbangles[2]));
  gcgQUATERNIONTOMATRIX4(m, quat);

  glPushMatrix();
  glTranslatef(obbcenter[0], obbcenter[1], obbcenter[2]); // Translating the OBB
  glMultMatrixf(m); // Rotating the AAB
  if(!gcgDrawAABox(obbmin, obbmax)) {
    gcgReport(GCG_REPORT_CODE(GCG_INFORMATION, GCG_DOMAIN_GRAPHICS, GCG_FUNCTIONCALLFAILED), "gcgDrawOBox(): gcgDrawAABox() failed. (%s:%d)", basename((char*)__FILE__), __LINE__);
    return false;
  }
  glPopMatrix();

  return true;
}

////////////////////////////////////////////////////
/************ Double precision version ************/
////////////////////////////////////////////////////
bool gcgDrawOBox(VECTOR3d obbangles, VECTOR3d obbcenter, VECTOR3d obbsize) {
  // Check parameters
  if(obbangles == NULL || obbcenter == NULL || obbsize == NULL) {
    gcgReport(GCG_REPORT_CODE(GCG_ERROR, GCG_DOMAIN_MEMORY, GCG_BADPARAMETERS), "gcgDrawOBox(): Invalid parameters. (%s:%d)", basename((char*)__FILE__), __LINE__);
    return false;
  }

  MATRIX4d m;
  VECTOR4d quat; // Quaternion

  obbsize[0] *= 0.5;
  obbsize[1] *= 0.5;
  obbsize[2] *= 0.5;
  VECTOR3d obbmin = {-obbsize[0], -obbsize[1], -obbsize[2]};
  VECTOR3d obbmax = {obbsize[0], obbsize[1], obbsize[2]};

  // Build a quaternion from Euler angles
  gcgEULERTOQUATERNION(quat, DEG2RAD(obbangles[0]), DEG2RAD(obbangles[1]), DEG2RAD(obbangles[2]));
  gcgQUATERNIONTOMATRIX4(m, quat);

  glPushMatrix();
  glTranslated(obbcenter[0], obbcenter[1], obbcenter[2]); // Translating the OBB
  glMultMatrixd(m); // Rotating the AAB
  if(!gcgDrawAABox(obbmin, obbmax)) {
    gcgReport(GCG_REPORT_CODE(GCG_INFORMATION, GCG_DOMAIN_GRAPHICS, GCG_FUNCTIONCALLFAILED), "gcgDrawOBox(): gcgDrawAABox() failed. (%s:%d)", basename((char*)__FILE__), __LINE__);
    return false;
  }
  glPopMatrix();

  return true;
}


