float gcgBlinkingAlpha() {
  float resto = (float) (clock() % CLOCKS_PER_SEC);
  return (float) ((resto < (CLOCKS_PER_SEC * 0.9)) ? 1.0 : 1.0 - (resto - (CLOCKS_PER_SEC * 0.9)) * (1.0 / (CLOCKS_PER_SEC * 0.10)));
}


